    requestedFrameWidth = params.input_video_width;
    requestedFrameHeight = params.input_video_height;
}


switch (params.title) {
    case "AVI":
        break;
    case "MP4":
        var index = (h264 ? 2 : 0) + (width320 ? 1 : 0);
        setVideoBitrate(bitrateQualityMap[params.title][index][qualityStop] * 1000);
        if (qualityStop == 4)
            params.ffmpeg_option_qmin = "";
        else if qualityStop == 0) {
            params.ffmpeg_option_maxrate = "40k";
            params.ffmpeg_option_bufsize = "5k";
            params.ffmpeg_option_g = "ntsc";
            params.output_video_frame_rate = "10";
        }
        break;
    case "QuickTime":
        break;
    case "WMV":
        if (qualityStop == 0) 
            setVideoBitrate(20000);
        else {
            var qualset = qualsetMap[params.title][qualityStop];
            var br = Math.round(((params.input_video_width) * (params.input_video_height)) / qualset);
            setVideoBitrate(br * 1000);
        }

        if (qualityStop == 0) {
            requestedFrameWidth = 240;
            params.ffmpeg_option_maxrate = "24k";
            params.ffmpeg_option_bufsize = "5k";
            params.ffmpeg_option_g = "ntsc";
            params.output_video_frame_rate = "10";
        }
        else if (width320)
            requestedFrameWidth = "320";
        else {
            requestedFrameWidth = params.input_video_width;
            requestedFrameHeight = params.input_video_height;
        }
        break;
    case "Flash":
        if (qualityStop == "0") 
            setVideoBitrate(30000);
        else {
             var qualset = qualsetMap[params.title][qualityStop];
             var br = Math.round(((params.input_video_width) * (params.input_video_height)) / qualset);
             setVideoBitrate(br * 1000);
        }

        if (qualityStop == 0) {
            requestedFrameWidth = "240";
            params.ffmpeg_option_maxrate = "34k";
            params.ffmpeg_option_bufsize = "5k";
            params.ffmpeg_option_g = 30;
        }
        else if (width320)
            requestedFrameWidth = "320";
        else {
            requestedFrameWidth = params.input_video_width;
            requestedFrameHeight = params.input_video_height;
        }
        break;
    case "MPEG":
        switch(params.profile) {
            case "none":
                setAudioParams((qualityStop == 0) ? "low" : "high";
                setVideoBitrate(mpegbitrateQualityMap[params.profile][qualityStop] * 1000);

                if (mpeg2) {
                    params.ffmpeg_option_vcodec = "mpeg2video";
                    params.output_video_codec_name = "MPEG-2";
                }
                else {
                    params.ffmpeg_option_vcodec = "mpeg1video";
                    params.output_video_codec_name = "MPEG-1";

                    params.ffmpeg_video_aspect_x = params.input_video_width;
                    params.ffmpeg_video_aspect_y = params.input_video_height;

                    params.ffmpeg_option_ratemax = mpegMaxrateMap["mpeg"][qualityStop];
                    params.ffmpeg_option_bufsize = mpegBufsizeMap["mpeg"][qualityStop];
                }
                break;
            case "vcd":
                params.ffmpeg_option_qmin = 5;
                params.ffmpeg_option_bf = 2;
                params.ffmpeg_option_target = isNTSC ? "ntsc-vcd" : "pal-vcd";
                params.ffmpeg_video_aspect_x = 4;
                params.ffmpeg_video_aspect_y = 3;

                if (params.input_video_aspect >= 1.2 && params.input_video_aspect <= 1.5) {
                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 240 : 288;
                }
                else if (params.input_video_aspect >= 1.6 && params.input_video_aspect <= 1.9) {
                    params.ffmpeg_option_padtop = isNTSC ? 32 : 48;
                    params.ffmpeg_option_padbottom = isNTSC ? 32 : 48;

                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 176 : 192;
                }
                else if (params.input_video_aspect >= 2 && params.input_video_aspect <= 2.5) {
                    params.ffmpeg_option_padtop = isNTSC ? 48 : 64;
                    params.ffmpeg_option_padbottom = isNTSC ? 48 : 64;
                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 144 : 160;
                }
                else {
                    requestedFrameWidth = "352";
                    requestedFrameHeight = "240";
                }
                break;
            case "svcd":
                setVideoBitrate(mpegbitrateQualityMap[params.profile][isNTSC ? 1 : 0][qualityStop] * 1000);

                params.ffmpeg_video_aspect_x = 4;
                params.ffmpeg_video_aspect_y = 3;

                if (params.input_video_aspect >= 1.2 && params.input_video_aspect <= 1.5) {
                    requestedFrameWidth = 480;
                    requestedFrameHeight = isNTSC ? 480 : 576;
                }
                else if (params.input_video_aspect >= 1.6 && params.input_video_aspect <= 1.9) {
                    params.ffmpeg_option_padtop = 64;
                    params.ffmpeg_option_padbottom = 64;
                    requestedFrameWidth = 480;
                    requestedFrameHeight = isNTSC ? 352 : 448;
                }
                else if (params.input_video_aspect >= 2 && params.input_video_aspect <= 2.5) {
                    params.ffmpeg_option_padtop = isNTSC ? 96 : 112;
                    params.ffmpeg_option_padbottom = isNTSC ? 96 : 112;
                    requestedFrameWidth = 480;
                    requestedFrameHeight = isNTSC ? 272 : 352;
                }
                else {
                    requestedFrameWidth = 480;
                    requestedFrameHeight = isNTSC ? 480 : 576;
                }
                break;
            case "cvd":
                setVideoBitrate(mpegbitrateQualityMap[params.profile][isNTSC ? 1 : 0][qualityStop] * 1000);

                params.ffmpeg_video_aspect_x = 4;
                params.ffmpeg_video_aspect_y = 3;

                if (params.input_video_aspect >= 1.2 && params.input_video_aspect <= 1.5) {
                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 480 : 576;
                }
                else if (params.input_video_aspect >= 1.6 && params.input_video_aspect <= 1.9) {
                    params.ffmpeg_option_padtop = 64;
                    params.ffmpeg_option_padbottom = 64;
                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 352 : 448;
                }
                else if (params.input_video_aspect >= 2 && params.input_video_aspect <= 2.5) {
                    params.ffmpeg_option_padtop = isNTSC ? 96 : 112;
                    params.ffmpeg_option_padbottom = isNTSC ? 96 : 112;
                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 272 : 352;
                }
                else{
                    requestedFrameWidth = 352;
                    requestedFrameHeight = isNTSC ? 480 : 576;
                }
                break;
            case "tivo":
                // Values are between ratios of 1.2 - 1.5 - 1.9 - 2.5 - > 2.5
                var index;
                if (params.input_video_aspect > 2.5)
                    index = 0;
                else if (params.input_video_aspect > 2)
                    index = 3;
                else if (params.input_video_aspect > 1.6)
                    index = 2;
                else if (params.input_video_aspect > 1.2)
                    index = 1;
                else
                    index = 0;

                requestedFrameWidth = tivoVideoSize[params.qualityStop][index][0];
                requestedFrameHeight = tivoVideoSize[params.qualityStop][index][1];
                setVideoBitrate(mpegbitrateQualityMap[params.profile][qualityStop] * 1000);

                params.ffmpeg_option_padtop = tivoVideoPadding[index][0];
                if (params.ffmpeg_option_padtop = < 0)
                    params.ffmpeg_option_padtop = "";

                params.ffmpeg_option_padbottom = tivoVideoPadding[index][1];
                if (params.ffmpeg_option_padbottom = < 0)
                    params.ffmpeg_option_padbottom = "";
                 break;
            case "ts":
                setVideoBitrate(mpegbitrateQualityMap[params.profile][isNTSC ? 1 : 0][qualityStop] * 1000);
                setAudioParams("high");
                params.ffmpeg_video_aspect_x = 4;
                params.ffmpeg_video_aspect_y = 3;
                requestedFrameWidth = "352";
                requestedFrameHeight = isNTSC ? "480" : "576";
                break;
            case "720":
            case "1080":
                setAudioParams("hd");
                setVideoBitrate(18500);
                params.ffmpeg_video_aspect_x = 16;
                params.ffmpeg_video_aspect_y = 9;

                if (params.profile == "720") {
                    requestedFrameWidth = 1280;
                    requestedFrameHeight = 720;

                    if (params.input_video_aspect >= 1.2 && params.input_video_aspect <= 1.5) {
                        params.ffmpeg_option_padleft = 160;
                        params.ffmpeg_option_padright = 160;
                        requestedFrameWidth = 960;
                    }
                    else if (params.input_video_aspect >= 2 && params.input_video_aspect <= 2.5) {
                        params.ffmpeg_option_padleft = 80;
                        params.ffmpeg_option_padright = 80;
                        requestedFrameHeight = 544;
                    }
                }
                else {
                    requestedFrameWidth = 1920;
                    requestedFrameHeight = 1080;

                    if (params.input_video_aspect >= 1.2 && params.input_video_aspect <= 1.5) {
                        params.ffmpeg_option_padleft = 240;
                        params.ffmpeg_option_padright = 240;
                        requestedFrameWidth = 1440;
                    }
                    else if (params.input_video_aspect >= 2 && params.input_video_aspect <= 2.5) {
                        params.ffmpeg_option_padleft = 128;
                        params.ffmpeg_option_padright = 128;
                        requestedFrameHeight = 816;
                    }
                }
                break;
        }
        break;

    case "DV":
        setVideoBitrate(bitrateQualityMap[params.title][width320 ? 1 : 0][qualityStop] * 1000);
        break;
}

setVideoSize(requestedFrameWidth, requestedFrameHeight);

// set audio quality
if(params.title == "MPEG") {

}else{
    audio(audioQualityMap[params.title][qualityStop]);
}

// The level is not necessarily accurate
params.output_video_profile_name = (params.title != "Apple TV") ? "baseline" : "main";
//params.output_video_level_name = levelMap[params.title][qualityStop];

// construct the recipe
if (     !bool(params.input_format == "Quicktime") && !bool(params.has_audio) && !bool(params.is_2_pass))
    params.recipe = "$normal_single_pass";
else if (!bool(params.input_format == "Quicktime") && !bool(params.has_audio) && bool(params.is_2_pass))
    params.recipe = "$normal_pass1 ; $normal_pass2";
else if (!bool(params.input_format == "Quicktime") && bool(params.has_audio) && !bool(params.is_2_pass))
    params.recipe = "$normal_single_pass";
else if (!bool(params.input_format == "Quicktime") && bool(params.has_audio) && bool(params.is_2_pass))
    params.recipe = "$normal_pass1 ; $normal_pass2";
else if (bool(params.input_format == "Quicktime") && !bool(params.has_audio) && !bool(params.is_2_pass))
    params.recipe = "$quicktime_v_single_pass";
else if (bool(params.input_format == "Quicktime") && !bool(params.has_audio) && bool(params.is_2_pass))
    params.recipe = "$quicktime_v_pass1 ; $quicktime_v_pass2";
else if (bool(params.input_format == "Quicktime") && bool(params.has_audio) && !bool(params.is_2_pass))
    params.recipe = "$quicktime_to_audio_file ; $quicktime_av_single_pass";
else if (bool(params.input_format == "Quicktime") && bool(params.has_audio) && bool(params.is_2_pass))
    params.recipe = "$quicktime_to_audio_file ; $quicktime_av_pass1 ; $quicktime_av_pass2";
