// The level is not necessarily accurate
params.output_video_profile_name = (title != "Apple TV") ? "baseline" : "main";
params.output_video_level_name = levelMap[title][qualityStop];

// construct the recipe
var recipeType = 
    (bool(params.input_format == "Quicktime") ? 4 : 0) |
    (bool(params.has_audio) ? 2 : 0) |
    (bool(params.is_2_pass) ? 1 : 0);

switch (recipeType) {
    case 0: params.recipe = "$normal_v_single_pass"; break;
    case 1: params.recipe = "$normal__v_pass1 ; $normal_v_pass2"; break
    case 2: params.recipe = "$normal_av_single_pass"; break;
    case 3: params.recipe = "$normal_av_pass1 ; $normal_av_pass2"; break;
    case 4: params.recipe = "$quicktime_v_single_pass"; break;
    case 5: params.recipe = "$quicktime_v_pass1 ; $quicktime_v_pass2"; break;
    case 6: params.recipe = "$quicktime_to_audio_file ; $quicktime_av_single_pass"; break;
    case 7: params.recipe = "$quicktime_to_audio_file ; $quicktime_av_pass1 ; $quicktime_av_pass2"; break;
}



// At this point, we know what 2 quality stops we are between (params.quality_stop and params.quality_stop+1)
// and we know what percentage of the way between the two we are (params.quality). We also which device we have 
