default_device.script:


function caseInsensitiveCompare(a,b) { return a.toLowerCase() == b.toLowerCase() }

function audio(type)
{
    switch(type) {
        case "low":    params.audio_bitrate =  16000; params.audio_sample_rate = 11025; params.audio_channels = 1; break;
        case "medium": params.audio_bitrate =  32000; params.audio_sample_rate = 22050; params.audio_channels = 1; break;
        case "high":   params.audio_bitrate = 128000; params.audio_sample_rate = 48000; params.audio_channels = 2; break;
    }
}

function all()
{
    for (var i = 0; i < arguments.length; ++i)
        if (!arguments[i])
            return false;
    return true;
}

function any()
{
    for (var i = 0; i < arguments.length; ++i)
        if (arguments[i])
            return true;
    return false;
}

function standardizeVideoCodecName(name)
{
    if (caseInsensitiveCompare(name, "vc-1"))
        return "wmv3";
    if (caseInsensitiveCompare(name, "avc") || caseInsensitiveCompare(name, "avc1"))
        return "h.264";
    return name;
}

params.special_qt = params.is_quicktime == "true" && params.input_video_codec == "wmv3";

log("hello there");
